let php = require('../lib/core/phpserver');
let utils = require('../lib/core/utils').Utils;

let options = utils.createOptions({

    host: '127.0.0.1',
    port: 7878,

});

describe("PHP Instance:", function () {

    it('should emit "connect" event', function () {

        const phpInstance = new php.PHPServer(options);

        phpInstance.on('connect', function (done) {
            done();
        })

    })

});
